if (Translator.BetterBibTeX && zotero.itemType === 'journalArticle') {
  if (tex.has.journal){
    tex.remove('journal');
    tex.add({ name: 'journal', value: zotero.autoJournalAbbreviation });
  }
else
  tex.add({ name: 'journal', value: zotero.autoJournalAbbreviation });
}

if (Translator.BetterBibTeX && zotero.itemType === 'preprint' && zotero.arXiv) {
  {
    tex.remove('journal');
    tex.add({ name: 'howpublished', value: 'arXiv:' + zotero.arXiv.id });
  }
}
