if (Translator.BetterBibLaTeX) {
  if (
    [
      "book",
      "bookSection",
      "journalArticle",
      "newspaperArticle",
      "thesis",
      "report",
    ].includes(item.itemType)
  ) {
    delete this.has.url;
    delete this.has.urldate;

    if (["book", "bookSection"].includes(item.itemType)) {
      delete this.has.number;
    }

    if (item.itemType == "journalArticle") {
      delete this.has.publisher;
    }

    if (item.itemType == "newspaperArticle") {
      delete this.has.location;
    }
  }

  // Remove number and pubstate for arXiv preprints
  if (item.DOI && item.DOI.includes("arXiv")) {
    delete this.has.number;
    delete this.has.pubstate;
  }

  // Remove number for statute items
  if (item.itemType == "statute") {
    delete this.has.number;
  }

  // Omit shortjournal if it's exactly the same as journaltitle
  {
    const jt = tex.has.journaltitle?.value;
    const sj = tex.has.shortjournal?.value;
    if (jt && sj && jt === sj) {
      delete this.has.shortjournal;
    }
  }

  // Check if more than 10% of the title is CJK
  // https://github.com/retorquere/zotero-better-bibtex/issues/2810
  for (const field of ["title", "shorttitle"]) {
    const value = tex.has[field]?.value;
    if (
      value &&
      value.replace(/[^\u4E00-\u9FFF]/g, "").length / value.length > 0.1
    ) {
      tex.add({ name: field, value, enc: "minimal" });
    }
  }
}

if (Translator.BetterCSLJSON) {
  delete csl.ISBN;
  delete csl.ISSN;

  if (
    [
      "book",
      "bookSection",
      "journalArticle",
      "newspaperArticle",
      "thesis",
      "report",
    ].includes(item.itemType)
  ) {
    delete csl.URL;
    delete csl["accessed"];
    delete csl.PMID;
    delete csl.PMCID;

    if (["book", "bookSection"].includes(item.itemType)) {
      delete csl.number;
    }

    if (item.itemType == "journalArticle") {
      delete csl.publisher;
    }

    if (item.itemType == "newspaperArticle") {
      delete csl["publisher-place"];
    }
  }

  // Remove number and status for arXiv preprints
  if (csl.DOI && csl.DOI.includes("arXiv")) {
    delete csl.number;
    delete csl.publisher;
  }

  // Remove number for statute items
  if (item.itemType == "statute") {
    delete csl.number;
  }

  if (csl.DOI && csl.URL) {
    delete csl.URL;
    delete csl["accessed"];
  }
  // Add file field for CSL JSON export
  csl.file = zotero.attachments.map((a) => a.localPath).join(";");

  // Omit shortjournal if it's exactly the same as journaltitle
  if (
    csl["container-title"] &&
    csl["container-title-short"] &&
    csl["container-title"] === csl["container-title-short"]
  ) {
    delete csl["container-title-short"];
  }
}
