preferences.postscript:
if (Translator.BetterBibLaTeX) {
  if ([ "book", "bookSection", "journalArticle", "newspaperArticle", "thesis" ].includes(item.itemType)) {
    delete this.has.url;
    delete this.has.urldate;
  }

  // if more than 50% of the title is CJK
  if (zotero.title && (zotero.title.replace(/[^一-鿿]/g, '').length / zotero.title.length) > 0.5) {
    // re-add the title field doing only minimal ascaping
    tex.add({ name: 'title', value: zotero.title, enc: 'minimal' })
  }
}
