preferences.postscript:
if (Translator.BetterBibLaTeX) {
  if (
    [
      "book",
      "bookSection",
      "journalArticle",
      "newspaperArticle",
      "thesis",
      "report",
    ].includes(item.itemType)
  ) {
    delete this.has.url;
    delete this.has.urldate;

    if (["book", "bookSection"].includes(item.itemType)) {
      delete this.has.number;
    }

    if (item.itemType == "newspaperArticle") {
      delete this.has.location;
    }
  }

  // Check if more than 10% of the title is CJK
  // https://github.com/retorquere/zotero-better-bibtex/issues/2810
  if (
    zotero.title &&
    zotero.title.replace(/[^\u4E00-\u9FFF]/g, "").length / zotero.title.length >
      0.1
  ) {
    // re-add the title field doing only minimal ascaping
    tex.add({ name: "title", value: zotero.title, enc: "minimal" });
  }
}

if (Translator.BetterCSLJSON) {
  delete csl.ISBN;
  delete csl.ISSN;

  if (
    [
      "book",
      "bookSection",
      "journalArticle",
      "newspaperArticle",
      "thesis",
      "report",
    ].includes(item.itemType)
  ) {
    delete csl.URL;
    delete csl["accessed"];
    delete csl.PMID;
    delete csl.PMCID;
  }

  if (csl.DOI && csl.URL) {
    delete csl.URL;
    delete csl["accessed"];
  }
}
