// covers both Translator.BetterBibTeX and Translator.BetterBibLaTeX
if (Translator.BetterTeX) {
  if (zotero.itemType === 'conferencePaper') {
    // For @inproceedings
    const { author, booktitle, title, doi, editor, isbn, location, pages, url, publisher, series, address, articleno, file, issn, keywords, numpages, qualityassured, readstatus, relevance, timestamp, year, ...rest } = tex.has
	tex.has = { author, booktitle, title, doi, editor, isbn, location, pages, url, publisher, series, address, articleno, file, issn, keywords, numpages, qualityassured, readstatus, relevance, timestamp, year, ...rest }
    Object.keys(tex.has).forEach(key => tex.has[key] === undefined && delete tex.has[key])
  } else if (zotero.itemType === 'journalArticle') {
    // For @article
    const { author, title, doi, issn, number, pages, volume, address, file, journal, keywords, month, publisher, qualityassured, readstatus, relevance, timestamp, year, ...rest } = tex.has
	tex.has = { author, title, doi, issn, number, pages, volume, address, file, journal, keywords, month, publisher, qualityassured, readstatus, relevance, timestamp, year, ...rest }
    Object.keys(tex.has).forEach(key => tex.has[key] === undefined && delete tex.has[key])
  } else if (zotero.itemType === 'bookSection') {
    // For @incollection
    const { author, booktitle, title, doi, edition, editor, isbn, pages, publisher, volume, address, file, issn, year, ...rest } = tex.has
	tex.has = { author, booktitle, title, doi, edition, editor, isbn, pages, publisher, volume, address, file, issn, year, ...rest }
    Object.keys(tex.has).forEach(key => tex.has[key] === undefined && delete tex.has[key])
  } else if (zotero.itemType === 'book') {
    // For @book
    const { author, title, doi, isbn, publisher, address, numpages, qualityassured, timestamp, year, ...rest } = tex.has
	tex.has = { author, title, doi, isbn, publisher, address, numpages, qualityassured, timestamp, year, ...rest }
    Object.keys(tex.has).forEach(key => tex.has[key] === undefined && delete tex.has[key])
  } else if (zotero.itemType === 'document') {
    // For @misc
    const { author, date, title, url, urldate, file, year, ...rest } = tex.has
	tex.has = { author, date, title, url, urldate, file, year, ...rest }
    Object.keys(tex.has).forEach(key => tex.has[key] === undefined && delete tex.has[key])
  } else if (zotero.itemType === 'thesis') {
    // For @phdthesis
    const { author, title, url, file, school, year, ...rest } = tex.has
	tex.has = { author, title, url, file, school, year, ...rest }
    Object.keys(tex.has).forEach(key => tex.has[key] === undefined && delete tex.has[key])
  }
}
